mapper.select:
SELECT id, NAME, CODE, idCard, mobile, sex, birthday, address, email, type, updater, updatime, DATE_FORMAT( updatime, '%Y-%m-%d %H:%i:%s' ) AS updatimeStr FROM lz_base_employee WHERE state = 1
AND name LIKE CONCAT ('%',#{name},'%' )
AND type =#{type}

SELECT count(1) FROM lz_base_employee WHERE state = 1
AND name LIKE CONCAT ('%',#{name},'%' )
AND type =#{type}
