mapper.select:
SELECT g.id, g.name, g.code, g.type, g.brand, g.unit, g.color, g.standard, g.material, g.buyPrice, g.salePrice, g.descs, g.updater, g.updatime, DATE_FORMAT( g.updatime, '%Y-%m-%d %H:%i:%s' ) AS updatimeStr, g.picture, i.file_path AS pictureUrl FROM lz_base_goods g LEFT JOIN lz_base_image i ON g.picture=i.id WHERE g.state = 1
AND g.name LIKE CONCAT ('%',#{goodsName},'%' )
AND g.code =#{goodsCode}
AND g.type =#{goodsType}
AND g.brand =#{brand}
AND g.color =#{color}
AND g.standard =#{standard}
AND g.material =#{material}
ORDER BY ${sortCode} ${sortRole} limit #{limit} offset #{offset}

SELECT
count(1)
FROM
lz_base_goods
WHERE
state = 1

    AND name LIKE CONCAT ('%',#{goodsName},'%' )


    AND code =#{goodsCode}


    AND type =#{goodsType}


    AND brand =#{brand}


    AND color =#{color}


    AND standard =#{standard}


    AND material =#{material}
