mapper.select:

select id, name, descs, state,typeid, typename, creater, creatime, updatetime, updater
from lz_base_param
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into lz_base_param (id, name, descs,
  state, typeid, typename, creater,
  creatime, updatetime, updater
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{descs,jdbcType=VARCHAR},
  #{state,jdbcType=INTEGER}, #{typeid,jdbcType=INTEGER},#{typename,jdbcType=VARCHAR}, #{creater,jdbcType=VARCHAR},
  now(), now(), #{updater,jdbcType=VARCHAR}
  )

mapper.select:
SELECT id, name, descs, typeid, typename, creater, creatime, DATE_FORMAT( creatime, '%Y-%m-%d %H:%i:%s' ) AS creatimeStr, updatetime, DATE_FORMAT( updatetime, '%Y-%m-%d %H:%i:%s' ) AS updatimeStr FROM lz_base_param WHERE state = 1
AND name LIKE CONCAT ('%',#{name},'%' )
AND typename = #{typename}
AND typeid = #{typeid}
ORDER BY ${sortCode} ${sortRole} limit #{limit} offset #{offset}

SELECT count(1) FROM lz_base_param WHERE state = 1
AND name LIKE CONCAT ('%',#{name},'%' )
AND typename = #{typename}
AND typeid = #{typeid}

mapper.update:
update lz_base_param
set state = 0,
updatetime = now(),
updater = #{updater,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
AND state = 1
