mapper.select:

select id, name, linkman, mobile, address, descs, state, updater, updatime
from lz_sale_customer
where id = #{id,jdbcType=INTEGER}

SELECT id, NAME, linkman, mobile, address, descs, updater, updatime, DATE_FORMAT( updatime, '%Y-%m-%d %H:%i:%s' ) AS updatimeStr FROM lz_sale_customer WHERE state = 1
AND name LIKE CONCAT ('%',#{name},'%' )
ORDER BY ${sortCode} ${sortRole} limit #{limit} offset #{offset}
